export const handleKeyCommand = command => {
  const { editorState } = this.state;
  const newState = RichUtils.handleKeyCommand(editorState, command);
  if (newState) {
    this.onChange(newState);
    return true;
  }
  return false;
};

export const nTab = e => {
  const maxDepth = 4;
  this.onChange(RichUtils.onTab(e, this.state.editorState, 4));
};

export const toggleBlockType = blockType => {
  this.onChange(RichUtils.toggleBlockType(this.state.editorState, blockType));
};

export const toggleInlineStyle = inlineStyle => {
  this.onChange(
    RichUtils.toggleInlineStyle(this.state.editorState, inlineStyle)
  );
};

// Custom overrides for "code" style.
export const styleMap = {
  HIGHLIGHT: {
    background: '#fffe0d'
  }
};

export function getBlockStyle(block) {
  switch (block.getType()) {
    case 'blockquote':
      return 'Doc-blockquote';
    default:
      return null;
  }
}

export class StyleButton extends React.Component {
  constructor() {
    super();
    this.onToggle = e => {
      e.preventDefault();
      this.props.onToggle(this.props.style);
    };
  }

  render() {
    let className = 'Editor-styleButton';
    if (this.props.active) {
      className += ' Editor-activeButton';
    }

    return (
      <span className={className} onMouseDown={this.onToggle}>
        {this.props.label}
      </span>
    );
  }
}
export const BLOCK_TYPES = [
  { label: 'H1', style: 'header-one' },
  { label: 'H2', style: 'header-two' },
  { label: 'H3', style: 'header-three' },
  { label: 'H4', style: 'header-four' },
  { label: 'H5', style: 'header-five' },
  { label: 'H6', style: 'header-six' },
  { label: 'Blockquote', style: 'blockquote' },
  { label: 'UL', style: 'unordered-list-item' },
  { label: 'OL', style: 'ordered-list-item' },
  { label: 'Code Block', style: 'code-block' }
];

export const BlockStyleControls = props => {
  const { editorState } = props;
  const selection = editorState.getSelection();
  const blockType = editorState
    .getCurrentContent()
    .getBlockForKey(selection.getStartKey())
    .getType();

  return (
    <div className='Editor-controls'>
      {BLOCK_TYPES.map(type => (
        <StyleButton
          key={type.label}
          active={type.style === blockType}
          label={type.label}
          onToggle={props.onToggle}
          style={type.style}
        />
      ))}
    </div>
  );
};

export var INLINE_STYLES = [
  { label: 'Bold', style: 'BOLD' },
  { label: 'Italic', style: 'ITALIC' },
  { label: 'Underline', style: 'UNDERLINE' },
  { label: 'Monospace', style: 'CODE' },
  { label: 'Highlight', style: 'highlight' }
];

export const InlineStyleControls = props => {
  var currentStyle = props.editorState.getCurrentInlineStyle();
  return (
    <div className='Editor-controls'>
      {INLINE_STYLES.map(type => (
        <StyleButton
          key={type.label}
          active={currentStyle.has(type.style)}
          label={type.label}
          onToggle={props.onToggle}
          style={type.style}
        />
      ))}
    </div>
  );
};
